<?phpnamespace Selenia\Platform\Models;use Electro\Exceptions\FlashMessageException;use Electro\Exceptions\FlashType;use Exception;class TranslationData{  const KEY_WIDTH = 24; //max.padding for saved keys  function save (array $data, $path)  {    $iniFile    = parse_ini_file ($path);    $newiniFile = array_merge ($iniFile, $data);    $f = @fopen ($path, 'w');    if ($f === false)      throw new FlashMessageException('Não foi possível abrir o seguinte ficheiro: ' . $path, FlashType::ERROR);    try {      foreach ($newiniFile as $key => $value) {        $v = str_replace ('"', '\\"', $value);        $v = str_replace ('&', '&amp;', $v);        $k = str_pad ($key, self::KEY_WIDTH);        fwrite ($f, "$k = \"$v\"\n");      }    }    catch (Exception $e) {      fclose ($f);      throw $e;    }    fclose ($f);  }  function get($sKey,$path)  {    if (fileExists($path))    {      $data = parse_ini_file($path);      return get($data,$sKey);    }  }}